// App.js
import React, { useState } from 'react';

const App = () => {
    const [formData, setFormData] = useState({
        complexion: '',
        travelLocation: '',
        gender: '',
        size: '',
        physique: ''
    });

    const [suggestions, setSuggestions] = useState([]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const response = await fetch('http://localhost:5000/api/suggestions', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(formData),
        });
        const data = await response.json();
        setSuggestions(data.styling);
        alert(data.message);
    };

    return (
        <div>
            <h1>Styling Suggestions</h1>
            <form onSubmit={handleSubmit}>
                <label>
                    Complexion:
                    <select name="complexion" onChange={handleChange}>
                        <option value="">Select</option>
                        <option value="light">Light</option>
                        <option value="medium">Medium</option>
                        <option value="dark">Dark</option>
                    </select>
                </label>
                <label>
                    Travel Location:
                    <input type="text" name="travelLocation" onChange={handleChange} />
                </label>
                <label>
                    Gender:
                    <select name="gender" onChange={handleChange}>
                        <option value="">Select</option>
                        <option value="male">Male</option>
                        <option value="female">Female</option>
                        <option value="other">Other</option>
                    </select>
                </label>
                <label>
                    Size:
                    <input type="text" name="size" onChange={handleChange} />
                </label>
                <label>
                    Physique:
                    <input type="text" name="physique" onChange={handleChange} />
                </label>
                <button type="submit">Get Suggestions</button>
            </form>

            {suggestions.length > 0 && (
                <div>
                    <h2>Suggestions:</h2>
                    <ul>
                        {suggestions.map((item, index) => (
                            <li key={index}>{item}</li>
                        ))}
                    </ul>
                </div>
            )}
        </div>
    );
};

export default App;
